mapper.select:
select

from spring_cloud.sys_dept


    and name like concat('%', #{name,jdbcType=VARCHAR}, '%')


    and phone like concat('%', #{phone,jdbcType=VARCHAR}, '%')


    and email like concat('%', #{email,jdbcType=VARCHAR}, '%')


    and status = #{status,jdbcType=INTEGER}


    and delete_status = #{deleteStatus,jdbcType=INTEGER}


    and remark like concat('%', #{remark,jdbcType=VARCHAR}, '%')

  and id != #{id,jdbcType=VARCHAR}

select count(1)
from spring_cloud.sys_staff
where dept_id = #{deptId,jdbcType=VARCHAR}

select d.id from sys_dept d left join sys_role_dept rd on d.id = rd.dept_id where rd.role_id = #{id,jdbcType=VARCHAR}
and d.id not in (select d.parent_id from sys_dept d inner join sys_role_dept rd on d.id = rd.dept_id and rd.role_id = #{id})
order by d.parent_id, d.dept_order
